import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

const questions = [
  {
    image: '',
    correctCoords: [48.8566, 2.3522], // Paris
  },
  {
    image: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Funsplash.com%2Ffr%2Fs%2Fphotos%2Fparis%252C-france&psig=AOvVaw1BYKWP7RnlUeXmNOMOhSwE&ust=1748072301862000&source=images&cd=vfe&opi=89978449&ved=0CBEQjRxqFwoTCIDqofaKuY0DFQAAAAAdAAAAABAE',
    correctCoords: [-8.3405, 115.0920], // Bali
  },
  {
    image: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Funsplash.com%2Ffr%2Fs%2Fphotos%2Fparis%252C-france&psig=AOvVaw1BYKWP7RnlUeXmNOMOhSwE&ust=1748072301862000&source=images&cd=vfe&opi=89978449&ved=0CBEQjRxqFwoTCIDqofaKuY0DFQAAAAAdAAAAABAE',
    correctCoords: [52.3676, 4.9041], // Amsterdam
  },
];

function GuessMap({ onGuess }: { onGuess: (coords: [number, number]) => void }) {
  useMapEvents({
    click(e) {
      onGuess([e.latlng.lat, e.latlng.lng]);
    },
  });
  return null;
}

export default function BirthdayGeoGame() {
  const [current, setCurrent] = useState(0);
  const [message, setMessage] = useState('');
  const [guessed, setGuessed] = useState(false);
  const [userGuess, setUserGuess] = useState<[number, number] | null>(null);

  const q = questions[current];

  function handleGuess(coords: [number, number]) {
    const [lat1, lng1] = coords;
    const [lat2, lng2] = q.correctCoords;
    const dist = Math.sqrt(Math.pow(lat1 - lat2, 2) + Math.pow(lng1 - lng2, 2));
    setUserGuess(coords);
    setGuessed(true);
    if (dist < 1.0) {
      setMessage("‚úÖ Tr√®s proche ! Bravo mon c≈ìur ‚ù§Ô∏è");
      setTimeout(() => {
        if (current + 1 < questions.length) {
          setCurrent(current + 1);
          setGuessed(false);
          setUserGuess(null);
          setMessage('');
        } else {
          setMessage("üéâ Joyeux 20 ans mon amour ! Tu es all√©e au bout de tous nos souvenirs. ‚ù§Ô∏è");
        }
      }, 2000);
    } else {
      setMessage("üòÖ Pas loin... essaie encore !");
    }
  }

  return (
    <div className="min-h-screen bg-pink-50 flex flex-col items-center justify-start py-10 px-4">
      <h2 className="text-2xl font-semibold text-pink-700 mb-6 text-center drop-shadow-md">
        Devine o√π cette photo a √©t√© prise !
      </h2>

      <img
        src={q.image}
        alt="Souvenir"
        className="w-full max-w-2xl rounded-xl shadow-lg mb-6 object-cover"
      />

      <div className="w-full max-w-4xl h-[400px] rounded-xl overflow-hidden shadow-md">
        <MapContainer
          center={[20, 0]}
          zoom={2}
          className="w-full h-full"
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <GuessMap onGuess={handleGuess} />
          {userGuess && (
            <Marker
              position={userGuess}
              icon={L.icon({
                iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
                iconSize: [25, 41],
                iconAnchor: [12, 41],
              })}
            />
          )}
        </MapContainer>
      </div>

      <p className="mt-6 text-lg text-center font-medium text-gray-700 bg-white px-4 py-2 rounded-lg shadow-sm">
        {message}
      </p>
    </div>
  );
}
