import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

const questions = [
  {
    image: '',
    correctCoords: [48.8566, 2.3522], // Paris
  },
  {
    image: 'https://via.placeholder.com/800x400?text=Photo+2',
    correctCoords: [-8.3405, 115.0920], // Bali
  },
  {
    image: 'https://via.placeholder.com/800x400?text=Photo+3',
    correctCoords: [52.3676, 4.9041], // Amsterdam
  },
];

function GuessMap({ onGuess }: { onGuess: (coords: [number, number]) => void }) {
  useMapEvents({
    click(e) {
      onGuess([e.latlng.lat, e.latlng.lng]);
    },
  });
  return null;
}

export default function BirthdayGeoGame() {
  const [current, setCurrent] = useState(0);
  const [message, setMessage] = useState('');
  const [guessed, setGuessed] = useState(false);
  const [userGuess, setUserGuess] = useState<[number, number] | null>(null);

  const q = questions[current];

  function handleGuess(coords: [number, number]) {
    const [lat1, lng1] = coords;
    const [lat2, lng2] = q.correctCoords;
    const dist = Math.sqrt(Math.pow(lat1 - lat2, 2) + Math.pow(lng1 - lng2, 2));
    setUserGuess(coords);
    setGuessed(true);
    if (dist < 1.0) {
      setMessage("Bravo, tu √©tais tr√®s proche ‚ù§Ô∏è !");
      setTimeout(() => {
        if (current + 1 < questions.length) {
          setCurrent(current + 1);
          setGuessed(false);
          setUserGuess(null);
          setMessage('');
        } else {
          setMessage("üéâ Joyeux 20 ans mon amour ! Tu es all√©e au bout de tous nos souvenirs. ‚ù§Ô∏è");
        }
      }, 2000);
    } else {
      setMessage("Pas mal, mais essaie encore üòò !");
    }
  }

  return (
    <div className="text-center p-4">
      <h2 className="text-xl font-bold mb-4 bg-black-800">Devine o√π cette photo a √©t√© prise !</h2>
      <img
        src={q.image}
        alt="Souvenir"
        className="w-[90%] rounded-lg mb-4 mx-auto"
      />
      <MapContainer
        center={[20, 0]}
        zoom={2}
        className="h-[400px] w-[90%] mx-auto rounded-lg"
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <GuessMap onGuess={handleGuess} />
        {userGuess && (
          <Marker
            position={userGuess}
            icon={L.icon({
              iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
              iconSize: [25, 41],
              iconAnchor: [12, 41],
            })}
          />
        )}
      </MapContainer>
      <p className="mt-4">{message}</p>
    </div>
  );
}
