import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import './handwriting.css';

const questions = [
  {
    image: 'photo1.jpg',
    correctCoords: [48.8566, 2.3522], // Paris
  },
  {
    image: 'photo2.jpg',
    correctCoords: [-8.3405, 115.0920], // Bali
  },
  {
    image: 'photo3.jpg',
    correctCoords: [52.3676, 4.9041], // Amsterdam
  },
];

function GuessMap({ onGuess }: { onGuess: (coords: [number, number]) => void }) {
  useMapEvents({
    click(e) {
      onGuess([e.latlng.lat, e.latlng.lng]);
    },
  });
  return null;
}

export default function BirthdayGeoGame() {
  const [current, setCurrent] = useState(0);
  const [message, setMessage] = useState('');
  const [guessed, setGuessed] = useState(false);
  const [userGuess, setUserGuess] = useState<[number, number] | null>(null);
  const [started, setStarted] = useState(false);
  const [finished, setFinished] = useState(false);

  const q = questions[current];

  function handleGuess(coords: [number, number]) {
    const [lat1, lng1] = coords;
    const [lat2, lng2] = q.correctCoords;
    const dist = Math.sqrt(Math.pow(lat1 - lat2, 2) + Math.pow(lng1 - lng2, 2));
    setUserGuess(coords);
    setGuessed(true);
    if (dist < 1.0) {
      setMessage("✅ Très proche ! Bravo mon cœur ❤️");
      setTimeout(() => {
        if (current + 1 < questions.length) {
          setCurrent(current + 1);
          setGuessed(false);
          setUserGuess(null);
          setMessage('');
        } else {
          setFinished(true);
        }
      }, 2000);
    } else {
      setMessage("😅 Pas loin... essaie encore !");
    }
  }

  if (!started) {
    return (
      <div className="min-h-screen bg-pink-50 flex flex-col items-center justify-center px-4">
        <div className="text-center space-y-6 max-w-xl font-handwriting text-xl text-pink-800">
          <p>
            Bonjour mon cœur 💖<br />
            Aujourd'hui, tu souffles ta 20e bougie... 🕯️<br />
            Je voulais marquer le coup avec une surprise un peu spéciale.<br />
            Un petit voyage dans nos souvenirs les plus doux... ✨
          </p>
          <p>
            Laisse-toi porter, clique sur “Commencer” et amuse-toi à retrouver nos lieux...<br />
            Je t’aime fort, toujours. 💌
          </p>
          <button
            onClick={() => setStarted(true)}
            className="bg-pink-500 text-white px-6 py-3 rounded-lg font-semibold shadow-md hover:bg-pink-600 transition"
          >
            🎁 Commencer
          </button>
        </div>
      </div>
    );
  }

  if (finished) {
    return (
      <div className="min-h-screen bg-pink-50 flex items-center justify-center text-center px-4">
        <div className="bg-white p-8 rounded-xl shadow-xl max-w-xl font-handwriting text-pink-700 text-2xl">
          <h2 className="text-3xl font-bold mb-4">🎉 Joyeux 20 ans mon amour !</h2>
          <p>
            Tu es allée au bout de tous nos souvenirs...<br />
            Et comme toujours, tu m’as bluffé ❤️<br />
            Merci d’être toi, simplement.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-pink-50 flex flex-col items-center justify-start py-10 px-4">
      <h2 className="text-2xl font-semibold text-pink-700 mb-6 text-center drop-shadow-md font-handwriting">
        Devine où cette photo a été prise !
      </h2>

      <img
        src={q.image}
        alt="Souvenir"
        className="w-full max-w-2xl rounded-xl shadow-lg mb-6 object-cover h-64 sm:h-80 md:h-96"
      />

      <div className="w-full max-w-4xl h-[400px] rounded-xl overflow-hidden shadow-md">
        <MapContainer
          center={[20, 0]}
          zoom={2}
          className="w-full h-full"
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <GuessMap onGuess={handleGuess} />
          {userGuess && (
            <Marker
              position={userGuess}
              icon={L.icon({
                iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
                iconSize: [25, 41],
                iconAnchor: [12, 41],
              })}
            />
          )}
        </MapContainer>
      </div>

      <p className="mt-6 text-lg text-center font-medium text-gray-700 bg-white px-4 py-2 rounded-lg shadow-sm">
        {message}
      </p>
    </div>
  );
}
